      const shape = list[0];
      if (this._hasType(shape, this.ns.raml.vocabularies.shapes + 'ScalarShape')) {
        this._xmlProcessUnionScalarProperty(doc, node, property, shape);
      } else {
        this._xmlProcessProperty(doc, node, shape);
      }
      return;
