    let raw = this._raw;
    const type = typeof raw;
    if (['string', 'boolean', 'undefined'].indexOf(type) !== -1) {
      return raw;
    }
    if (raw && raw.type === 'Buffer') {
      raw = new Uint8Array(raw.data);
    }
